	if ($('body').hasClass('page-template-blog')){

	  $('#blog_content').isotope({
		 itemSelector: 'article',
		 resizable: true,
		 masonry: {
			columnWidth: '.grid-sizer'
		 }
	  });
	} else {
	  $('#blog_content').isotope({
		 itemSelector: 'article',
		 resizable: true,
	  });
		$(".blog .content").isotope("layout")
	}

	 if ($('.blog .content').hasClass('animated')){
						
			 var masonryChild = $('.blog .content').find('article');
		 
			  masonryChild.each(function(i){
					setTimeout(function(){
						$(".blog .content").isotope("layout")
						 masonryChild.eq(i).addClass('post-loaded animate');
					}, 200 * (i+1));
			  });
	 }	 


var items = $('.blog .content article');
    var ias = $.ias({
      container: '.blog .content',
      item: "article",
      pagination: "#pagination",
      next: ".next a",
      delay: 1200
    });

ias.on('rendered', function(items) {
      
      var $items = $(items);
            $items.imagesLoaded(function(){
              
              $(".blog .content").isotope( 'insert', $items );
					$items.addClass('post-loaded animate');
            });

});
    //ias.extension(new IASSpinnerExtension());
